import React , {useState} from 'react'
import Form from './Form'
import Todo from './Todo'


function App(){
    const [todoData, settodoData] = useState([{
        text: 'homeworks',
        state : true
    }]);

    const Todo = todoData.map( (todos) => 
        <Todo text = {todos.text} state = {todos.state}/>
    )

    return(
        <div>
            <Form props function = {handleClick.bind()}/> 
            <Todo text = {todoData.text} state = {todoData.state}/>
            <button onClick = { () => settodoData(handleClick(todoData))}/>
        </div>
    )
}
function handleClick(todoData){
    const temp = Object.assign([],{todoData});
    temp.push({
        text: 'dinner',
        state : true 
    })
    return temp
}

export default App


