import React , {useState} from 'react'
import PropTypes from 'prop-types';

function Todo(props){
    const [state,setState] = useState(props.state);
    const isVisible = props.visible;
    const propTypes = {
        func: PropTypes.func,
    };

    return(
            <div>
                {isVisible === 'true' && <p>{props.text} : id {props.id} </p>}
                {isVisible === 'true' && 
                <button onClick = {() => props.updataFunc(props.id)}> {state} </button>}
            </div>

    )

}


export default Todo
