import React ,{useEffect}from 'react'
import {connect} from 'react-redux'
import Todo from './Todo'


function Display(props){
    const highTask = dealTasks(props.todoDatas,props.view,'High')
    const middleTask = dealTasks(props.todoDatas,props.view,'Middle')
    const lowTask = dealTasks(props.todoDatas,props.view,'Low')
    useEffect( () => {
        highTask = dealTasks(props.todoDatas,props.view,'High')
        middleTask = dealTasks(props.todoDatas,props.view,'Middle')
        lowTask = dealTasks(props.todoDatas,props.view,'Low') 
    })
    const inLine = {
        display: 'inline-block',
        padding : '50px',
        width : '200px',
        verticalAlign:  'top'
    }
    return(
        <div>
            <div style = {inLine}>
                High
                {highTask}
            </div>
            <div style = {inLine}>
                Middle
                {middleTask}
            </div>
            <div style = {inLine}>
                Low
                {lowTask} 
            </div>
        </div>
    )
}
function dealTasks(todoDatas,view,priority){
    const Task = todoDatas.map( todos =>
        (view === 'All' || todos.state === view) && todos.priority === priority? 
        <div>
            <Todo id = {todos.id} key = {todos.id} text = {todos.text} state = {todos.state} priority = {todos.priority} visible = 'true' /> 
        </div>:
        <div>
            <Todo id = {todos.id} key = {todos.id} text = {todos.text} state = {todos.state} priority = {todos.priority} visible = 'false'/> 
        </div>
    )
    return Task
}
const mapStateToProps = state =>{
    return{
        todoDatas : state.todoDatas,
        view : state.view
    }
}
const mapDispatchToProps = dispatch => {
    return{

    }
}
export default connect(mapStateToProps,mapDispatchToProps)(Display)
