import React from 'react'
import {connect} from 'react-redux'
import Todo from './Todo'

function TodoList(props){
    const Task = props.todoDatas.map( todos => {
        console.log(todos);
        return (props.view === 'All' || todos.state === props.view) && todos.priority === props.priority? 
        <div>
            <Todo  key = {todos.id}  visible={true} todoData={todos}/> 
        </div>:
        <div>
            <Todo  key = {todos.id} visible={false} todoData={todos}/> 
        </div>}
    )

    return (
        <div>
            {Task}    
        </div>
    )
}
const mapStateToProps = state =>{
    return{
        todoDatas : state.todoDatas,
        view : state.currentView
    }
}
export default connect(mapStateToProps)(TodoList)
