import React , {useState, useEffect} from 'react'
import Todo from './Todo'


function App(){
    const [todoDatas, setTodoDatas] = useState([
        {
            id: 1,
            text: 'homeworks',
            state : 'Active'
        },
        {
            id: 2,
            text: 'homework',
            state : 'Active'
        }

    ]);
    const index = 3;
    const [inputText, setInputText] = useState("")
    const [view,setView] = useState('All');
    let tasks = todoDatas.map( (todos) => 
        view === 'All' || todos.state === view ? 
        <Todo id = {todos.id} key = {todos.id} text = {todos.text} state = {todos.state} visible = 'true'
            func = {() => setTodoDatas()}/> : 
        <Todo id = {todos.id} key = {todos.id} text = {todos.text} state = {todos.state} visible = 'false'/>
    ) 
    useEffect(() => {
        tasks = todoDatas.map( (todos) => 
            view === 'All' || todos.state == view ? 
                <Todo id = {todos.id} key = {todos.id} text = {todos.text} state = {todos.state} visible = 'true' /> : 
                <Todo id = {todos.id} key = {todos.id} text = {todos.text} state = {todos.state} visible = 'false' />
        )  
        console.log(tasks)  
    });

    return(
        <div>
            <button onClick = {() => setView ('All')}> All </button>
            <button onClick = {() => setView ('Active')}> Active </button> 
            <button onClick = {() => setView ('Done')}> Done </button>  
            <br />
            <input type = 'text' value = {inputText} onChange={event => setInputText(event.target.value)}/>
            <button onClick = { () => setTodoDatas( 
                [...todoDatas,
                {
                    id : index,
                    text: inputText,
                    state : 'Active' 
                }]) 
            }/>
            {tasks}
        </div>
    )
}
function handleChange(todoDatas,id){
    const todoCopy = todoDatas.slice();
    todoCopy[i] = {
        id : todoCopy[i].id,
        text : todoCopy[i].text,

    }
}


export default App


