import React , {useState, useEffect} from 'react'
import Todo from './Todo'


function App(){
    const [todoDatas, setTodoDatas] = useState([
        {
            id: 1,
            text: 'homeworks',
            state : 'Active'
        },
        {
            id: 2,
            text: 'homework',
            state : 'Active'
        }

    ]);
    const [inputText, setInputText] = useState("")
    const [view,setView] = useState('All');
    let tasks = todoDatas.map( (todos) => 
        view === 'All' || todos.state === view ? 
        <div>
            <Todo id = {todos.id} key = {todos.id} text = {todos.text} state = {todos.state} visible = 'true'/> 
            <button onClick ={ ()=> setTodoDatas(handleChange(todoDatas,todos.id))}> Check </button>
        </div>:
        <div>
            <Todo id = {todos.id} key = {todos.id} text = {todos.text} state = {todos.state} visible = 'false'/> 

        </div>
    ) 
    useEffect(() => {
        tasks = todoDatas.map( (todos) => 
            view === 'All' || todos.state == view ? 
            <div>
                <Todo id = {todos.id} key = {todos.id} text = {todos.text} state = {todos.state} visible = 'true'/> 
                <button onClick ={ ()=> setTodoDatas(handleChange(todoDatas,todos.id))}> Check </button>
            </div>:
            <div>
                <Todo id = {todos.id} key = {todos.id} text = {todos.text} state = {todos.state} visible = 'false'/> 

            </div>
        )  

    });

    return(
        <div>
            <button onClick = {() => setView ('All')}> All </button>
            <button onClick = {() => setView ('Active')}> Active </button> 
            <button onClick = {() => setView ('Done')}> Done </button>  
            <br />
            <input type = 'text' value = {inputText} onChange={event => setInputText(event.target.value)}/>
            <button onClick = { () => setTodoDatas(setTodo(todoDatas,inputText))}> Insert </button> 
            {tasks}
        </div>
    )
}

function handleChange(todoDatas,id){
    const todoCopy = todoDatas.slice();
    /*
    todoCopy[id - 1] = {
        id : todoCopy[id - 1].id,
        text : todoCopy[id - 1].text,
        state : todoCopy[id - 1].state === 'Active' ? 'Done' : 'Active'
    }
    */
   todoCopy[id -1].state = todoCopy[id - 1].state === 'Active' ? 'Done' : 'Active';
   return todoCopy
}
function setTodo(todoDatas,inputText){
    return (
        [...todoDatas,
            {
                id : todoDatas.length + 1,
                text: inputText,
                state : 'Active' 
            }
        ]
    )
}
/*
<button onClick = { () => setTodoDatas( 
                [...todoDatas,
                {
                    id : index,
                    text: inputText,
                    state : 'Active' 
                }]) 
            }/>
*/


export default App
