import React , {useState} from 'react'

function Todo(props){
    const [state,setState] = useState(props.state);
    return(
        <div>
            <p>{props.text}</p>
            <button onClick = { () => setState(state == 'Active' ? 'Done' : 'Active')}> {state} </button>
        </div>
    )

}

export default Todo
