    return todoCopy
}

function insertTodo(todoDatas,inputText,priority){
    if(!inputText.match(/\S/g)){
        return (
            todoDatas
        )
    }else{
        return (
            [...todoDatas,
                {
                    key : todoDatas.length + 1,
                    id : todoDatas.length + 1,
                    text: inputText,
                    state : 'Active',
                    priority : priority
                }
            ]
        )
    }
}

