const initialState = {
    todoDatas : [],
    view : 'All'
}
const reducer = (state = initialState, action) => {
    if(action.type === 'CHANGE_VIEW_TO_ALL'){
        const newState = state;
        newState.view = 'All'
        return newState
    }
    if(action.type === 'CHANGE_VIEW_TO_ACTIVE'){
        const newState = state;
        newState.view = 'Active'
        return newState
    }
    if(action.type === 'CHANGE_VIEW_TO_DONE'){
        const newState = state;
        newState.view = 'Done'
        return newState
    }
    if(action.type === 'INSERTTASK'){
       const newState = state
       newState.todoDatas = 
            [...newState.todoDatas,
                {
                    key : newState.todoDatas.length + 1,
                    id : newState.todoDatas.length + 1,
                    text: action.payload.Text,
                    state : 'Active',
                    priority : action.payload.priority
                }
            ]
        return newState
    }
    return state
}




export default reducer
