import React , {useState} from 'react'

function Todo(props){
    const [text,setText] = useState(props.text);
    const [state,setState] = useState(props.state);
    return(
        <div>
            <p>{text}</p>
            <button onClick = { () => setState(state)}> {state} </button>
        </div>
    )

}

export default Todo
