import React , {useState} from 'react'
import Form from './Form'
import Todo from './Todo'

const [todoData, settodoData] = useState({
    text: 'homeworks',
    state : true
});
function App(){

    const [state, setState] = useState(1);
    return(
        <div>
            <Form props function = {handleClick.bind()}/> 
            <Todo text = {todoData.text} state = {todoData.state}/>
            <button onClick = {() => handleClick() }/>
        </div>
    )
}
function handleClick(){
    const temp = Object.assign([],{todoData});
    temp.push({
        text: 'dinner',
        state : true 
    })
    settodoData(temp);
}

export default App


