import React , {useState, useEffect} from 'react'
import Todo from './Todo'


function App(){
    const [todoDatas, setTodoDatas] = useState([
        {
            text: 'homeworks',
            state : 'Active'
        },
        {
            text: 'homework',
            state : 'Active'
        }

    ]);
    const [inputText, setInputText] = useState("")
    const [view,setView] = useState('All');
    let tasks;
    useEffect(() => {
        tasks = todoDatas.map( (todos) => 
            view == 'All' || todos.state == view ? <Todo text = {todos.text} state = {todos.state} visible = 'true'/> : 
            <Todo text = {todos.text} state = {todos.state} visible = 'false'/>
        )  
        console.log(tasks)  
    });

    return(
        <div>
            <button onClick = {() => setView ('All')}> All </button>
            <button onClick = {() => setView ('Active')}> Active </button> 
            <button onClick = {() => setView ('Done')}> Done </button>  
            <br />
            <input type = 'text' value = {inputText} onChange={event => setInputText(event.target.value)}/>
            <button onClick = { () => setTodoDatas(
                [...todoDatas,
                {
                    text: inputText,
                    state : 'Active' 
                }]) 
            }/>
            {tasks}
        </div>
    )
}


export default App


