import React , {useState} from 'react'
import Form from './Form'
import Todo from './Todo'


function App(){
    const [todoDatas, settodoDatas] = useState([
        {
            text: 'homeworks',
            state : false
        },
        {
            text: 'homework',
            state : true
        }

    ]);
    const tasks = todoDatas.map( (todos) => 
        <Todo text = {todos.text} state = {todos.state}/>
    )
    return(
        <div>
            <Form props function = {handleClick.bind()}/> 
            {tasks}
            <button onClick = { () => settodoDatas(handleClick(todoDatas))}/>
        </div>
    )
}
function handleClick(todoDatas){
    console.log(todoDatas);
    todoDatas.push({
        text: 'dinner',
        state : true 
    })
    return todoDatas
}

export default App


