import React, {useCallback}  from 'react'
import {connect} from 'react-redux'


function Todo(props){
    const onClick = () => props.changeState(props.todo.id)

    const style = {
        color : todo.priority === 'High' ? 'red' : 'black'
    }

    return(
            <div>
                <p style = {style}> {props.todo.text} : {props.todo.isActive ? 'Active' : 'Done'} </p>}
                <button style = {style} onClick ={onClick}>Check</button>}
            </div>
    )

}

const mapDispatchToProps = dispatch =>{
    return {
        changeState : (id) => dispatch({type:'CHANGE_STATE', id:id})
    }
}

export default connect(null,mapDispatchToProps)(Todo)
