        return {
            view : 'All'
        }
    }
    if(action.type === 'CHANGE_VIEW_TO_ACTIVE'){
        return {
            view : 'Active'
        }
    }
    if(action.type === 'CHANGE_VIEW_TO_DONE'){
        return {
            view : 'Done'
        }
    }
    return state
}

export default reducer
