import React from 'react'
import Todo from './Todo'

function Display(){
    const highTask = dealTasks('High')
    const middleTask = dealTasks('Middle')
    const lowTask = dealTasks('Low')
    const inLine = {
        display: 'inline-block',
        padding : '50px',
        width : '200px',
        verticalAlign:  'top'
    }
    return(
        <div>
            <div  style = {inLine}>
                High
                {highTask}
            </div>
            <div style = {inLine}>
                Middle
                {middleTask}
            </div>
            <div style = {inLine}>
                Low
                {lowTask} 
            </div>
        </div>
    )
}
function dealTasks(priority){
    const Task = todoDatas.map( todos =>
        (props.view === 'All' || todos.state === view) && todos.priority === priority? 
        <div>
            <Todo id = {todos.id} key = {todos.id} text = {todos.text} state = {todos.state} priority = {todos.priority} visible = 'true'/> 
        </div>:
        <div>
            <Todo id = {todos.id} key = {todos.id} text = {todos.text} state = {todos.state} priority = {todos.priority} visible = 'false'/> 
        </div>
    )
    return Task
}

export default Display
