import {connect} from 'react-redux'

function Filter(){
    const [view,setView] = useState('All')
    return(
        <div>
            <p>{view}</p>
            <button onClick = {mapDispatchToProps}> All </button>
            <button onClick = {mapDispatchToProps}> Active </button> 
            <button onClick = {mapDispatchToProps}> Done </button>  
        </div>
    )
}
const mapStateToProps = state => {
    return{
        view : props.view
    }
}
const mapDispatchToProps = dispatch => {
    return {
        changeViewToAll : () => dispatch({type : 'CHANGE_VIEW_TO_ALL'}),
        changeViewToActive : () => dispatch({type : 'CHANGE_VIEW_TO_ACTIVE'}),
        changeViewToDone : () => dispatch({type : 'CHANGE_VIEW_TO_DONE'})
    }
}

export default connect(mapStateToProps,mapDispatchToProps)(Filter)
