import React, {useState, useEffect} from 'react'
import {connect} from 'react-redux'

function Filter(){
    const [view,setView] = useState();
    useEffect( () => {
        setView('All')
    })
    return(
        <div>
            <button onClick = {() => setView ('All')}> All </button>
            <button onClick = {() => setView ('Active')}> Active </button> 
            <button onClick = {() => setView ('Done')}> Done </button>  
        </div>
    )
}

const mapDispatchToProps = dispatch => {
    return {
        changeViewToHigh : () => dispatch({type : 'CHANGETOHIGH'}),
        changeViewToMiddle : () => dispatch({type : 'CHANGETOMIDDLE'}),
        changeViewToLow : () => dispatch({type : 'CHANGETOLOW'})
    }
}

export default connect(null,mapDispatchToProps)(Filter)
