import React , {useState} from 'react'
import Form from './Form'
import Todo from './Todo'


function App(){
    const [todoDatas, settodoDatas] = useState([
        {
            text: 'homeworks',
            state : true
        },
        {
            text: 'homework',
            state : true
        }

    ]);
    /*
    const Todo = todoDatas.map( (todos) => 
        <Todo text = {todos.text} state = {todos.state}/>
    )
    */

    return(
        <div>
            <Form props function = {handleClick.bind()}/> 
            <Todo text = {todoDatas[0].text} state = {todoDatas[0].state}/>
            <button onClick = { () => settodoDatas(handleClick(todoDatas))}/>
        </div>
    )
}
function handleClick(todoData){
    const temp = Object.assign([],{todoData});
    temp.push({
        text: 'dinner',
        state : true 
    })
    return temp
}

export default App


